import axios from 'axios'

const EMPLOYEE_BASE_REST_API_URL = 'http://localhost:8081/api/v1/employee';
const AuthStr = 'Bearer '.concat("demoApiKey"); 


class EmployeeService{

    getAllEmployees(){
        return axios.get(EMPLOYEE_BASE_REST_API_URL, { headers: { Authorization: AuthStr }})
    }

    createEmployee(employee){
        return axios.post(EMPLOYEE_BASE_REST_API_URL + "/create", employee, { headers: { Authorization: AuthStr } })
    }

    getEmployeeById(employeeId){
        return axios.get(EMPLOYEE_BASE_REST_API_URL + '/' + employeeId, { headers: { Authorization: AuthStr } });
    }

}

export default new EmployeeService();
